# EJERCICIO 3 RELACION PVI #clear all;clc;more off;function y=f1(t,x)  y(1)=x(1)-t*x(2)+sin(3*t);  y(2)=2*x(1)+x(2)-t;  endfunction%x0=[5,-1];t=0;h=0.25;a=0; b=3;n=ceil((b-a)/h);figure(1); clf; hold on;for i=1:n  aux=x0+h*f1(t,x0);  xE(1,i)=aux(1);  xE(2,i)=aux(2);  t=t+h;  x0=aux;  endfor%plot(xE(1,:),xE(2,:),'b;Euler;');t=0;x0=[5,-1];for i=1:n  K1=f1(t,x0);  K2=f1(t+h/2,x0+h/2*K1);  K3=f1(t+h/2,x0+h/2*K2);  K4=f1(t+h,x0+h*K3);  aux=x0+h/6*(K1+2*K2+2*K3+K4);  xRK(1,i)=aux(1);  xRK(2,i)=aux(2);  t=t+h;  x0=aux;  endfor%plot(xRK(1,:),xRK(2,:),'r;RK;');hold off;