#### EJERCICIO PROPUESTO 3 ####clc;clear all;%APARTADO Adisp('APARTADO A');%DEFINICIÓN DE LA FUNCIÓNfunction y=f(x)  y=-x.^3/9-1;  endfunction%x0=-1.5; x1=f(x0); x2=f(x1);a0=x0-(x1-x0)^2/(x2-2*x1+x0);s0=a0;%FORMATO TABLA RESULTADOSdisp('ITERACION FUNCIONAL  ACELERACION AITKEN  ACELERACION STEFFENSEN');formato = '%18.14f';printf([formato '\n'],x0);printf([formato '\n'],x1);printf([formato formato formato '\n'],x2,a0,s0);for n= 1:3  for m=1:2  x0=x1; x1=x2; x2=f(x1);  a0=x0-(x1-x0)^2/(x2-2*x1+x0);  printf([formato formato '\n'],x2,a0);
  endfor
x0=x1; x1=x2; x2=f(x1);a0=x0-(x1-x0)^2/(x2-2*x1+x0);s1=f(x0);s2=f(s1);s0=s0-(s1-s0)^2/(s2-2*s1+s0);printf([formato formato formato '\n'],x2,a0,s0);endfor%disp('El metodo de Aitken es rapidamente convergente, pero Steffenson lo es aun mas.');%APARTADO Bdisp('APARTADO B');%DEFINICION FUNCIONfunction y=f1(x)  y=x^3+10*x+9;endfunction%TOL=10^(-8);x0=-1.5;disp('STEFFENSEN');x1=f1(x0);x2=f1(x1);s0=x0-(x1-x0)^2/(x2-2*x1+x0);printf([formato '\n'],s0);x0=s0;s1=s0;dos0=s1;x1=f1(x0);x2=f1(x1);s1=x0-(x1-x0)^2/(x2-2*x1+x0);printf([formato '\n'],s1);x0=s1;until abs(s1-s0)<TOL