## EJERCICIO 7 ##clc;clear all;disp('EJERCICIO 7 RELACION PROBLEMAS');%Definición funcionesfunction y=f(x)  y=[4*x(1)^2+x(2)^2-4;x(1)+x(2)-sin(x(1)-x(2))]; endfunction%function M=J(x)   M=[8*x(1),2*x(2);1-cos(x(1)-x(2)),1+cos(x(1)-x(2))];  endfunction%X0=[0.5;0.5];aux=[0.5;0.5];TOL=1e-08;n=0;do  n++;  X0=aux;  X1=X0-J(X0)\f(X0);  aux=X1;until norm(X1-X0,1)<TOL%printf('La solución en %3d iteraciones es x= %14.10f e y= %14.10f\n',n,X1);