## EJERCICIO 8 RELACION DE EJERCICIOS ##clear all;clc;disp('Ejercicio 8. Algoritmo de Müller.');function y=f(x)  y=1-x+0.3*cos(x);  endfunction%function y=Muller(x0,x1,x2)  d0=(f(x1)-f(x0))/(x1-x0);  d1=(f(x2)-f(x1))/(x2-x1);  a=(d1-d0)/(x2-x0);  b=d0-a*(x0+x1);  c=f(x0)-x0*d0+a*x0*x1;  y=(-b-sqrt(b^2-4*a*c))/(2*a);  endfunction%x0=1.3;x1=1.4;x2=1.5;disp('Tres valores iniciales y luego las iteraciones:');printf('%14.10f\n',x0);printf('%14.10f\n',x1);printf('%14.10f\n',x2);for i=1:8  x=Muller(x0,x1,x2);  printf('%14.10f\n',x);  x0=x1;  x1=x2;  x2=x;  endfor%